import React, { useState } from "react";
import axios from "axios";
import { Container, TextField, Button, Typography, CircularProgress, Paper, Chip, Box } from "@mui/material";

const UploadForm = () => {
  const [file, setFile] = useState(null);
  const [jobDescription, setJobDescription] = useState("");
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file || !jobDescription) {
      alert("Please upload a file and enter a job description.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);
    formData.append("job_description", jobDescription);

    try {
      setLoading(true);
      const res = await axios.post("http://127.0.0.1:5000/analyze", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setResponse(res.data);
    } catch (error) {
      console.error("Error uploading file:", error);
      setResponse({ error: "Failed to analyze resume" });
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="sm">
      <Paper elevation={3} style={{ padding: "20px", marginTop: "30px" }}>
        <Typography variant="h5" gutterBottom>
          Upload Your Resume (PDF)
        </Typography>
        <form onSubmit={handleSubmit}>
          <input type="file" accept=".pdf" onChange={handleFileChange} style={{ marginBottom: "10px" }} />
          <TextField
            label="Job Description"
            multiline
            rows={4}
            fullWidth
            value={jobDescription}
            onChange={(e) => setJobDescription(e.target.value)}
            margin="normal"
            variant="outlined"
          />
          <Button type="submit" variant="contained" color="primary" fullWidth disabled={loading} style={{ marginTop: "10px" }}>
            {loading ? <CircularProgress size={24} /> : "Upload & Analyze"}
          </Button>
        </form>

        {response && (
          <Paper style={{ padding: "15px", marginTop: "20px", background: "#f9f9f9" }}>
            <Typography variant="h6">Results:</Typography>
            {response.error ? (
              <Typography color="error">{response.error}</Typography>
            ) : (
              <div>
                <Typography><strong>Summary:</strong> {response.summary}</Typography>
                <Typography><strong>Score:</strong> <Chip label={`${response.score}/100`} color="primary" /></Typography>
                
                {response.missing_skills && response.missing_skills.length > 0 && (
                  <Box mt={2}>
                    <Typography color="error"><strong>Missing Skills:</strong></Typography>
                    {response.missing_skills.map((skill, index) => (
                      <Chip key={index} label={skill} color="error" style={{ margin: "2px" }} />
                    ))}
                  </Box>
                )}
              </div>
            )}
          </Paper>
        )}
      </Paper>
    </Container>
  );
};

export default UploadForm;
