document.getElementById("upload-form").onsubmit = async function(event) {
    event.preventDefault();

    let formData = new FormData();
    formData.append("file", document.getElementById("file").files[0]);
    formData.append("job_description", document.getElementById("job_description").value);

    try {
        let response = await fetch("http://127.0.0.1:5000/analyze", {
            method: "POST",
            body: formData,
        });
        let data = await response.json();

        if (data.error) {
            alert(data.error);
        } else {
            // Display the response data (summary, score, missing skills)
            document.getElementById("summary").textContent = data.summary;
            document.getElementById("score").textContent = data.score;
            document.getElementById("missing-skills").textContent = data.missing_skills.join(", ");
        }
    } catch (error) {
        console.error("Error:", error);
        alert("An error occurred during the analysis.");
    }
};
